sprites.onOverlap(SpriteKind.Player, SpriteKind.LifeUp, function (sprite, otherSprite) {
    if (statusbar.value == 5) {
        story.spriteSayText(sprite, "I'm fine, I don't need this")
    } else {
        statusbar.value += 10
        otherSprite.destroy()
        story.spriteSayText(sprite, "Just what I needed")
        music.powerUp.play()
    }
})
