sprites.onOverlap(SpriteKind.Player, SpriteKind.Enemy, function (sprite, otherSprite) {
    if (sprite.y <= otherSprite.y) {
        music.knock.play()
        statusbar.value += -1
        sprite.vx = -300
        if (info.score() <= 0) {
            info.setScore(0)
        } else {
            info.changeScoreBy(-5)
        }
    } else if (sprite.y > otherSprite.y) {
        music.powerUp.play()
        otherSprite.destroy()
        sprite.vy = -300
        if (info.score() <= 0) {
            info.setScore(10)
        } else {
            info.changeScoreBy(5)
        }
    }
})
